import {defineStore} from "pinia";

export const useStore = defineStore('store', {
  state: () => ({
    shopId: parseInt(localStorage.getItem('shopId')) || null,
    doctors:[],
    patients:[],
    reports:[]
  }),
  actions: {
    createDoctor(doctor){
      console.log(this.doctors);
      this.doctors.push({ 
        ...doctor,
      })
      console.log(this.doctors);
    },
    updateDoctor(doctor){
      const index = this.doctors.findIndex(d => d.id === doctor.id);
      this.doctors[index] = doctor
    },
    deleteDoctor(doctor){
      const index = this.doctors.findIndex(d => d.id === doctor.id);
      this.doctors.slice(index, 1)
    },

    deletePatient(patient){
      const index = this.patients.findIndex(p => p.id === patient.id);
      this.patients.slice(index, 1)
    },
    updatePatient(patient){
      const index = this.patients.findIndex(p => p.id === patient.id);
      this.patients[index] = this.patient
    },
    createPatients(patient){
      let patientslength = this.patients.length
      this.patients.push({ 
        id:patientslength + 1,
        ...patient
      })    
    }
  },
});
