import axios from "axios";

axios.defaults.baseURL = `${import.meta.env.BASE_URL}/api`;

function Request({url,...options}) {
    const token = localStorage.getItem('_zToken');
    options.headers = {
        ...options.headers,
        Authorization: token && `Bearer ${token}`
    }
    return axios({
        url: url,
        ...options
    });
}

export default Request;
export {Request};
