import Request from '@/api';
export default {
  __name: 'DoctorForm',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const doctorId = ref(null);
    const ruleForm = reactive({
      doctorName: '',
      workTime: null,
      surname: '',
      category: '',
      serviceCost: ''
    });
    const rules = reactive({
      doctorName: [{
        required: true,
        message: 'Iltimos ism kiriting',
        trigger: 'blur'
      }],
      surname: [{
        required: true,
        message: 'Iltimos familiya kiriting',
        trigger: 'blur'
      }],
      category: [{
        required: true,
        message: 'Iltimos kategoriya kiriting',
        trigger: 'blur'
      }],
      serviceCost: [{
        required: true,
        message: 'Iltimos xizmat narxini kiriting',
        trigger: 'blur'
      }],
      workTime: [{
        type: 'date',
        required: true,
        message: 'Iltimos ishlash vaqtini kiriting',
        trigger: 'change'
      }]
    });
    const submitForm = async formEl => {
      if (!formEl) return;
      await formEl.validate((valid, fields) => {
        if (valid) {
          // if(doctorId.value){
          console.log('sss');
          // useDoctors().updateDoctor(ruleForm);
          // }else{
          useDoctors().createDoctor(ruleForm);
          // }
          router.go(-1);
        } else {
          console.log('error submit!', fields);
        }
      });
    };
    const getOneDoctor = id => {
      Request({
        url: `doctor/${id}`,
        method: "get"
      }).then(res => {
        ruleForm.doctorName = res.data.name;
        ruleForm.category = res.data.category;
        ruleForm.serviceCost = res.data.serviceCost;
        ruleForm.surname = res.data.surname;
        ruleForm.workTime = res.data.workTime;
      }).catch(error => {
        useToast().error(error);
      });
    };
    const resetForm = formEl => {
      if (!formEl) return;
      formEl.resetFields();
    };
    onMounted(() => {
      if (router.currentRoute.value.params.id) {
        doctorId.value = router.currentRoute.value.params.id;
        getOneDoctor(doctorId.value);
      }
    });
    const __returned__ = {
      doctorId,
      ruleForm,
      rules,
      submitForm,
      getOneDoctor,
      resetForm,
      onMounted,
      reactive,
      ref,
      get useDoctors() {
        return useDoctors;
      },
      get useToast() {
        return useToast;
      },
      get router() {
        return router;
      },
      TheComponentHeader,
      get Request() {
        return Request;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
