import {defineStore} from "pinia";
import Request from "@/api";
import { useToast } from "./toast.pinia";

export const useDoctors = defineStore('doctors', {
    state: () => ({
        doctors:null
    }),
    actions: {
        createDoctor(form){
            Request({
                url:"doctor",
                method:"post",
                data:form
            }).then((res) => {
                console.log(res);
            }).catch((error) => {
                useToast().error(error)
            })
        },
        getAllDoctors(form){
            Request({
                url:"doctor",
                method:"get",
                data:form
            }).then((res) => {
                console.log(res);
            }).catch((error) => {
                useToast().error(error)
            })
        }
    }
});
